#!/bin/bash

# Script to encode Android keystore for GitHub Secrets
# This helps you prepare your keystore for CI/CD

set -e

if [ $# -eq 0 ]; then
    echo "🔐 Android Keystore Encoder for GitHub Secrets"
    echo "=============================================="
    echo ""
    echo "Usage: $0 <path-to-keystore.jks>"
    echo ""
    echo "Example:"
    echo "  $0 src-tauri/gen/android/keystore/release-key.jks"
    echo ""
    echo "This script will:"
    echo "  1. Encode your keystore to base64"
    echo "  2. Show you how to add it to GitHub Secrets"
    echo ""
    exit 1
fi

KEYSTORE_PATH="$1"

if [ ! -f "$KEYSTORE_PATH" ]; then
    echo "❌ Error: Keystore file not found: $KEYSTORE_PATH"
    exit 1
fi

echo "🔐 Encoding keystore for GitHub Secrets"
echo "======================================="
echo ""
echo "Keystore: $KEYSTORE_PATH"
echo ""

# Check if base64 is available
if ! command -v base64 &> /dev/null; then
    echo "❌ Error: base64 command not found"
    exit 1
fi

# Encode the keystore
echo "Encoding keystore to base64..."
BASE64_ENCODED=$(base64 -i "$KEYSTORE_PATH" 2>/dev/null || base64 "$KEYSTORE_PATH")

echo ""
